sections.css:
$blue: #3372df;
$blueSecondary: lighten($blue, 40%);
$green: #0f9d58;
$greenSecondary: lighten($green, 30%);
$red: #cb4437;
$redSecondary: lighten($red, 30%);
$yellow: #f4b400;
$yellowSecondary: lighten($yellow, 20%);
$white: white;
$gray: #737373;
$grayLight: #eeeeee;
$grayDark: #404040;

$colorText: $grayDark;
$colorHighlight: $blue;
$colorWarning: $yellowSecondary;
$colorMuted: $gray;
$colorDanger: $red;

// Defining font family
$fontDefault: 'Open Sans', Helvetica, Arial, sans-serif;
$fontHeader: 'PT Serif', Georgia, serif;

// Defining font weight
$fontWeight: normal;
$fontWeightBold: 900;
$fontHeaderWeight: 700;

// Defining baseline line height
$lineHeight: 1.5;

// Defining animation easings
$animationEasing: cubic-bezier(0.455, 0.030, 0.515, 0.955) !default;

// Base spacing
$baseSpacing: 1.5rem;

.sg-style {
  font-family: $fontDefault;
  font-weight: $fontWeight;
  line-height: $lineHeight;
  color: $colorText;
}

html {
  font-size: 100%;
  @extend .sg-style;

  @include breakpoint(tablet) {
    font-size: 110%;
  }

  @include breakpoint(desktop) {
    font-size: 120%;
  }

  @include breakpoint(largeScreen) {
    font-size: 130%;
  }
}

h1, h2, h3, h4, h5, h6 {
  font-family: $fontHeader;
  font-weight: $fontHeaderWeight;
  color: $blue
}

.yotta,
.zetta {
  font-family: $fontDefault;
}

.yotta {
  font-size: 2.2807rem;
  line-height: 1.3154;
  color: $colorHighlight;
  font-weight: $fontWeightBold;
  text-transform: uppercase;
}

.zetta {
  font-size: 2.0273rem;
  line-height: 1.4798;
  color:  $colorHighlight;
}

h1, .exa {
  font-size: 1.8020rem;
  line-height: 1.6648;
}

h2, .peta {
  font-size: 1.6018rem;
  line-height: 1.8729;
}

h3, .tera {
  font-size: 1.4238rem;
  line-height: 1.0535;
}

h4, .giga {
  font-size: 1.2656rem;
  line-height: 1.1852;
}

h5, .mega {
  font-size: 1.1250rem;
  line-height: 1.3333;
}

h6, .kilo {
  font-size: 1.0000rem;
  line-height: 1.5000;
}

body {
    margin: 0;
    padding: 0;
    position: absolute;
    width: 100%;
    height: 100%;
}

main {
    @include container;
}

.section {
    @extend .pad-bottom-double;
    @include full;
    @extend .clearfix;
    position: relative;
    clear: both;
}

/*
   #Nav
   ========================================================================== */

.navbar {
    @include full;
    .brand {
        @include span(full);
        @include breakpoint(phablet) {
            @include span(6);
        }

        @include breakpoint(laptop) {
            @include span(9);
        }
    }
    > ul li {
        @include span(4 of $nav_layout);
        @include breakpoint(phablet) {
            @include span(2 of $nav_layout);
        }
        @include breakpoint(laptop) {
            @include span(1 of $nav_layout);
        }
    }
}

/*
   #About
   ========================================================================== */
.about {
    .about__wrapper {
        @include container(50em);
    }
    .about__img {
        @include span(full);
        @include breakpoint(phablet) {
            @include span(4);
        }
    }
    .about__text {
        @include span(full);
        text-align: center;
        @include breakpoint(phablet) {
            @include span(8);
            text-align: left;
        }
    }
}

/*
   #Skills
   ========================================================================== */

.skill {
    text-align: center;
    @include breakpoint(tablet) {
        @include span(4);
    }
}


/*
   #Projects
   ========================================================================== */

.section.projects {
    padding-bottom: 0;
}

.project {
    @include full;
}

.project__img {
    @include breakpoint(phablet) {
        @include span(4);
    }
}
.project__info {
    @include breakpoint(phablet) {
        @include span(8);
    }
}


/*
   Design Examples
   ========================================================================== */

.examples {
    .examples__wrapper {
       @include span(12);
    }
    .examples__imgs {
        @include breakpoint(tablet) {
            li {
                @include span(6 of $gallery_layout);
            }
        }
    }
}


/*
   Footer
   ========================================================================== */

.social-wrap {
    @include span(full);
}

.thanks {
    @include span(12)
}

/*
 * Hide visually and from screen readers:
 */

.hidden {
  display: none !important;
}

/*
 * Hide only visually, but have it available for screen readers:
 * http://snook.ca/archives/html_and_css/hiding-content-for-accessibility
 */

.visuallyhidden {
  border: 0;
  clip: rect(0 0 0 0);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  width: 1px;
}

/*
 * Extends the .visuallyhidden class to allow the element
 * to be focusable when navigated to via the keyboard:
 * https://www.drupal.org/node/897638
 */

.visuallyhidden.focusable:active,
.visuallyhidden.focusable:focus {
  clip: auto;
  height: auto;
  margin: 0;
  overflow: visible;
  position: static;
  width: auto;
}

/*
 * Hide visually and from screen readers, but maintain layout
 */

.invisible {
  visibility: hidden;
}

/*
 * Clearfix: contain floats
 *
 * For modern browsers
 * 1. The space content is one way to avoid an Opera bug when the
 *    `contenteditable` attribute is included anywhere else in the document.
 *    Otherwise it causes space to appear at the top and bottom of elements
 *    that receive the `clearfix` class.
 * 2. The use of `table` rather than `block` is only necessary if using
 *    `:before` to contain the top-margins of child elements.
 */

.clearfix {
  &:before,
  &:after {
    content: " "; /* 1 */
    display: table; /* 2 */
  }

  &:after {
    clear: both;
  }
}

.text-left {
  text-align: left;
}

.text-right {
  text-align: right;
}

.text-center {
  text-align: center;
}

.normal {
  font-weight: normal;
}

.bold {
  font-weight: bold;
}

.italic {
  font-style: italic;
}

.push {
  margin-bottom: 1.5rem;
}

.push-none, .push-0 {
  margin-bottom: 0;
}

.push-double, .push-2 {
  margin-bottom: 3.0000rem;
}

.push-half, .push-1-2 {
  margin-bottom: 0.7500rem;
}

.push-quarter, .push-1-4 {
  margin-bottom: 0.3750rem;
}

.pad-top, .pad-t {
  padding-top: 1.5rem;
}

.pad-bottom, .pad-b {
  padding-bottom: 1.5rem;
}

.pad-top-double, .pad-t-2 {
  padding-top: 3.0000rem;
}

.pad-bottom-double, .pad-b-2 {
  padding-bottom: 3.0000rem;
}

.pad-top-half, .pad-t-1-2 {
  padding-top: 0.7500rem;
}

.pad-bottom-half, .pad-b-1-2 {
  padding-bottom: 0.7500rem;
}

.pad-top-quarter, .pad-t-1-4 {
  padding-top: 0.3750rem;
}

.pad-bottom-quarter, .pad-b-1-4 {
  padding-bottom: 0.3750rem;
}

.island {
  padding: 1.5rem;
}

.island-double, .island-2 {
  padding: 3.0000rem;
}

.island-half, .island-1-2 {
  padding: 0.7500rem;
}

.island-quarter, .island-1-4 {
  padding: 0.3750rem;
}

.gutter {
  padding-left: 1.5rem;
  padding-right: 1.5rem;
}

.gutter-double, .gutter-2 {
  padding-left: 3.0000rem;
  padding-right: 3.0000rem;
}

.gutter-half, .gutter-1-2 {
  padding-left: 0.7500rem;
  padding-right: 0.7500rem;
}

.gutter-quarter, .gutter-1-4 {
  padding-left: 0.3750rem;
  padding-right: 0.3750rem;
}

